pragma solidity 0.8.4;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "./EIP712MetaTransaction.sol";
import "./interfaces/ISugarBounceToken.sol";
import "./libraries/TransferHelper.sol";

contract BuyCredit is EIP712MetaTransaction("BuyCredit", "1"), ReentrancyGuard {
    using Address for address;
    address public immutable SB_TOKEN;

    // solhint-disable-next-line var-name-mixedcase
    bytes32 private immutable _BUY_CREDIT_TYPEHASH =
        keccak256("BuyCredit(address from,address to,uint256 amount,uint256 deadline,uint256 salt)");

    mapping(address => uint256) private nonces;

    event TransferSB(address indexed from, address indexed to, uint256 amount);

    constructor(address _SB_TOKEN) {
        require(_SB_TOKEN.isContract(), "It should be deployed smart contract");
        SB_TOKEN = _SB_TOKEN;
    }

    function getBuyCreditNonces(address user) external view returns (uint256) {
        return nonces[user];
    }

    function permitSBToken(
        address owner,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external {
        ISugarBounceToken(SB_TOKEN).permit(owner, address(this), value, deadline, v, r, s);
    }

    function buyCredit(
        address from,
        address to,
        uint256 amount,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external nonReentrant {
        require(deadline > block.timestamp, "Transfer signature is expired");

        bytes32 msgHash = keccak256(abi.encode(_BUY_CREDIT_TYPEHASH, from, to, amount, deadline, nonces[from]));

        // bytes32 msgHash = keccak256(abi.encodePacked(productName));
        bytes32 digest = toTypedMessageHash(msgHash);
        // (bytes32 r, bytes32 s, uint8 v) = splitSignature(sig);
        address recoveredAddress = ecrecover(digest, v, r, s);

        require(recoveredAddress == from, "Invalid signer dfgdgdfgdf");

        TransferHelper.safeTransferFrom(SB_TOKEN, from, to, amount);

        nonces[from]++;

        emit TransferSB(from, to, amount);
    }
}
